<?php
 namespace Compassplus\Sdk; use Compassplus\Sdk\Config\Config; use Compassplus\Sdk\Request\DataProvider; use Exception; use GuzzleHttp\Client; use GuzzleHttp\Message\FutureResponse; use GuzzleHttp\Message\ResponseInterface; use GuzzleHttp\Ring\Future\FutureInterface; use InvalidArgumentException; use SplFileInfo; class Connector { public $orderData; private $certPassword; private $secureConnectionOnly = true; private $url; private $pathToKey; private $keyPassword; private $pathToCaCertFile; private $debug = false; private $pathToCertFile; public function __construct($host = null, $port = null, $endpoint = '') { $this->setCaCert(__DIR__ . '/cacert.pem'); if (!empty($host)) { if ($port !== null) { $this->url = 'https://' . $host . ':' . $port . $endpoint; } else { $this->url = 'https://' . $host . $endpoint; } } } public function debug() { $this->debug = true; } public function sendRequest() { $url = $this->getUrl(); error_log(print_r($url, 1)); $body = $this->orderData; return $this->getResponse($url, $body); } private function getUrl() { if (empty($this->url)) { if (Config::getPort()) { $url = Config::getHostName() . ':' . Config::getPort() . '/exec'; } else { $url = Config::getHostName() . '/exec'; } if (!strpos($url, '://')) { $url = 'https://' . $url; } return $url; } return $this->url; } private function getResponse($url, $body) { return $this->send($url, $body); } private function send($url, $body) { $caCertFileName = 'cacert.pem'; ini_set('curl.cainfo', $this->pathToCaCertFile); $client = new Client(); $cert = ''; if ($this->certPassword !== null) { $cert = [$this->pathToCertFile, $this->certPassword]; } else { $cert = $this->pathToCertFile; } $options = [ 'debug' => true, 'body' => $body, 'verify' => $this->pathToCaCertFile, 'cert' => $cert, 'config' => [ 'curl' => [ CURLOPT_SSL_VERIFYHOST => 2, CURLOPT_SSL_VERIFYPEER => true ] ], 'allow_redirects' => [ 'max' => 5, 'strict' => true, 'referer' => true, 'protocols' => ['https', 'http'], ] ]; if (!$this->secureConnectionOnly) { $options['protocols'] = ['https', 'http']; } if (!empty($this->pathToKey)) { if (isset($this->keyPassword)) { $options['ssl_key'] = [$this->pathToKey, $this->keyPassword]; } else { $options['ssl_key'] = $this->pathToKey; } } if (is_bool($this->debug) && $this->debug) { $options['debug'] = true; } if (is_bool($this->debug) && $this->debug) { error_log(json_encode([ "CAcert path" => dirname(__DIR__) . DIRECTORY_SEPARATOR . $caCertFileName, "Cert path" => $this->pathToCertFile, "Key path" => $this->pathToKey, "URL" => $url, "Body" => $body ]), 0); } print_r($options); $response = $client->post($url, $options); return $response; } public function setUnsecuredConnection() { $this->secureConnectionOnly = false; } public function setCert($pathToCert, $password = null) { $this->pathToCertFile = $this->getRealPath($pathToCert); if ($password !== null) { $this->certPassword = $password; } } public function setKey($pathToKey, $password = null) { $this->pathToKey = $this->getRealPath($pathToKey); if ($password !== null) { $this->keyPassword = $password; } } private function getRealPath($pathToFile) { $info = new SplFileInfo($pathToFile); if (!$info->isFile()) { throw new InvalidArgumentException(sprintf('Cert file not found in: %s', $pathToFile)); } if (!$info->isReadable()) { throw new Exception(sprintf('Cert file %s not readable', $pathToFile)); } return $info->getRealPath(); } public function setCaCert($caCertFile) { $this->pathToCaCertFile = $this->getRealPath($caCertFile); } } 