<?php
 namespace Compassplus\Sdk\Request\XML; use Compassplus\Sdk\OrderPayment; use Compassplus\Sdk\Request\DataProvider; use Compassplus\Sdk\Service; use UnexpectedValueException; use function str_replace; class Payment extends DataProvider { public function getRequestData() { $service = new Service(); $requestBody = [ 'Request' => [ 'Operation' => 'CreateOrder', 'Language' => '', 'Order' => [ 'OrderType' => 'Payment', 'Merchant' => $this->operation->merchant->getId(), 'Amount' => $this->operation->order->getAmount(), 'Currency' => $this->operation->order->getCurrency(), 'Description' => $this->operation->order->getDescription(), 'ApproveURL' => $this->operation->merchant->getApproveUrl(), 'CancelURL' => $this->operation->merchant->getCancelUrl(), 'DeclineURL' => $this->operation->merchant->getDeclineUrl() ] ] ]; if (!empty($this->operation->customer->getEmail())) { $requestBody['Request']['Order']['email'] = $this->operation->customer->getEmail(); } if (!empty($this->operation->customer->getPhone())) { $requestBody['Request']['Order']['phone'] = $this->operation->customer->getPhone(); } $requestBody['Request']['Order']['AddParams'] = $this->getAddParams($this->operation->order); $xml = $service->write('TKKPG', $requestBody); if ($xml) { return $xml; } throw new UnexpectedValueException('XML is not generated'); } public function getAddParams(OrderPayment $orderExt) { $addParams = array(); $addParams['VendorID'] = $orderExt->getVendorId(); $addPaymentParams = $orderExt->getPaymentParams(); $addPaymentParams = str_replace(array('/', "\\"), array("\/", "\\\\"), $addPaymentParams); if (!empty($addPaymentParams)) { $addParamList = ''; foreach ($addPaymentParams as $paramData) { if (!empty($addParamList)) { $addParamList .= '/'; } $addParamList .= $paramData; } $addParams['PaymentParams'] = $addParamList; } return $addParams; } } 